import React, { Component } from 'react';
import firebase from './Firebase';
import { GoTrashcan, GoListUnordered } from 'react-icons/go';
import { FaLink } from 'react-icons/fa';
import { navigate } from '@reach/router';

class MeetingList extends Component {
  constructor(props) {
    super(props);
    this.deleteMeeting = this.deleteMeeting.bind(this);
  }

  deleteMeeting = (e, whichMeeting) => {
    e.preventDefault();
    const ref = firebase
      .database()
      .ref(`meetings/${this.props.userID}/${whichMeeting}`);
    ref.remove();
  }

  render() {
    const { meetings } = this.props;
    const myMeetings = meetings.map(item => {
      return(
        <div className="list-group-item d-flex" key={item.meetingID}>
          <section
            className="btn-group align-self-center"
            role="group"
            aria-label="Meeting Options"
          >
            <button
              className="btn btn-sm btn-outline-secondary"
              title="Delete Meeting"
              onClick={e => this.deleteMeeting(e, item.meetingID)}
            >
              <GoTrashcan />
            </button>
            <button
              className="btn btn-sm btn-outline-secondary"
              title="Check In"
              onClick={() =>
                navigate(
                  `/checkin/${this.props.userID}/${item.meetingID}`
                )
              }
            >
              <FaLink />
            </button>
            <button
              className="btn btn-sm btn-outline-secondary"
              title="Meetings List"
              onClick={() =>
                navigate(
                  `/checkin/${this.props.userID}/${item.meetingID}`
                )
              }
            >
              <FaLink />
            </button>
          </section>
          <section className="pl-3 text-left align-elf-center">
            {item.meetingName}
          </section>
        </div>
      )
    })
    return (
      <div>
        {myMeetings}
      </div>
    );
  }
}
export default MeetingList;
